// Print the index of all the alphabet 
package day7;

public class Ex2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		String st="Hello this is Himanshu";
		System.out.println("index of H is "+st.indexOf('H'));
		System.out.println("index of i is "+st.indexOf('i'));
		System.out.println("index of m is "+st.indexOf('m'));
		System.out.println("index of a is "+st.indexOf('a'));
	
	}

}
